	CREATE PROCEDURE `forgotpasswordcheck`(in `phonenumber` varchar(20),in `email` varchar(1024))
	begin
		if exists(select * from employee where contactnumber=`phonenumber` and officeemail=`email`) then select 1;
		else select 0;
		end if;

	end ;;
