	CREATE PROCEDURE `getemployeeroles`(
	in `employee_id` varchar(20)
	)
	begin
	SELECT r.id,r.name, e.rmid
	FROM employee_roles e,rolesmaster r
	WHERE e.role_id = r.id
	and e.employee_id = `employee_id`
	order by r.name;
