	DELIMITER ;;
	CREATE  PROCEDURE `get_announcements_topics_master`(in topicid int)
	begin
		select r.id,topic,r.status from ems_announcements_topics_master r where r.id <=> ifnull(topicid,id);
	end ;;
	DELIMITER ;
