	CREATE  PROCEDURE `get_hr_details`()
	begin

			select e.id,CONCAT(e.firstname, " ", e.middlename, " ", e.lastname) as managername 
				  ,e.empid,e.contactnumber , e.officeemail
			from  employee e 
