	CREATE  PROCEDURE `get_unread_messages_count`(in empid int)
	BEGIN
		   select COUNT(erm.id) as unreadcount
		   from  employee_recognition_message erm
			where erm.recognition_reciever_empid=`empid` and erm.recognition_isread=0 ;
	  END ;;
