	)
	begin
		DECLARE EXIT HANDLER FOR SQLEXCEPTION
		BEGIN
			ROLLBACK;
			-- SELECT 'An error has occurred, operation rollbacked and the stored procedure was terminated' AS Message;
			select -1 as successstate;
		END; 
		set @successstate = 0;
