			json_unquote(json_extract(candidatedata,"$.address")),
			@city,
			@state,
			json_unquote(json_extract(candidatedata,"$.pincode")),
			@country,
			json_unquote(json_extract(candidatedata,"$.paddress")),
			@pcity,
			@pstate,
			json_unquote(json_extract(candidatedata,"$.ppincode")),
			@pcountry,
