	CREATE  PROCEDURE `update_ems_induction_conductedby_status`(
	in `id` int(11),
	in `status` int(11)
	)
	begin
		update ems_induction_conductedby set ems_induction_conductedby.status=`status`
		where ems_induction_conductedby.id=`id`;
		select 0 as statuscode;
	end ;;
