						and ((weekoffs_table.effective_fromdate <= datetable.ldate and datetable.ldate <= weekoffs_table.effective_todate)
							or (datetable.ldate >= effective_fromdate and effective_todate is null));   
					end if;
				end if;
				
				set @j = @j + 1;
				end while;
