					if (vrulename = 'LEAVES_MIN_DAYS_PRIOR_APPLICATION' and vrulevalue > (datediff(`fromdate`,curdate())) and (`fromdate` >= curdate())) then
						set @prior_count = (select fn_get_min_days_prior_value_for_leave_type(`leavetype_id`));
